String formatTimestamp(String timestamp) {
  final DateTime dateTime = DateTime.parse(timestamp);
  final Duration difference = DateTime.now().difference(dateTime);
  final int minutes = difference.inMinutes;

  if (minutes < 1) {
    return 'just now';
  } else if (minutes == 1) {
    return '1 min ago';
  } else if (minutes < 60) {
    return '$minutes mins ago';
  } else {
    final int hours = minutes ~/ 60;
    if (hours == 1) {
      return '1 hour ago';
    } else if (hours < 24) {
      return '$hours hours ago';
    } else {
      final int days = hours ~/ 24;
      if (days == 1) {
        return '1 day ago';
      } else {
        return '$days days ago';
      }
    }
  }
}


class CountCard extends StatelessWidget {
  final String title;
  final int count;
  final IconData icon;
  final Color color;

  CountCard({
    required this.title,
    required this.count,
    required this.icon,
    required this.color,
  });

  @override
  Widget build(BuildContext context) {
    return Card(
      elevation: 3.0,
      child: Padding(
        padding: const EdgeInsets.all(8.0),
        child: Column(
          mainAxisAlignment: MainAxisAlignment.center,
          crossAxisAlignment: CrossAxisAlignment.center,
          children: [
            Icon(
              icon,
              size: 40,
              color: color,
            ),
            SizedBox(height: 10),
            Text(
              title,
              style: TextStyle(fontSize: 18, fontWeight: FontWeight.bold),
            ),
            SizedBox(height: 5),
            Text(
              count.toString(),
              style: TextStyle(fontSize: 24, fontWeight: FontWeight.bold),
            ),
          ],
        ),
      ),
    );
  }
}


class DashboardWidget extends StatefulWidget {
  @override
  _DashboardWidgetState createState() => _DashboardWidgetState();
}

class _DashboardWidgetState extends State<DashboardWidget> {
  Map<String, dynamic> countData = {
    '_count': {
      'posts': 0,
      'comments': 0,
      'upvotes': 0,
      'feedbacks': 0,
    }
  };
  

  final List<String> carouselImages = [
    'data:image/jpeg;base64,',
    'data:image/jpeg;base64,',
    'data:image/jpeg;base64,',
    // Add more image URLs here
  ];

  int _currentIndex = 0;    

  @override
  void initState() {
    super.initState();
    fetchData(); // Fetch data when the widget is initialized
  }

    Future<void> fetchData() async {
    final prefs = await SharedPreferences.getInstance();
    String? userInfo = prefs.getString('userinfo');
    if (userInfo != null) {
      Map<String, dynamic> userInfoMap = json.decode(userInfo);
      var userid = userInfoMap['id'];
      final String apiUrl = "$backendUrl/api/user/count/$userid";
      
        try {
        final response = await http.get(Uri.parse(apiUrl));

        if (response.statusCode == 200) {
            final jsonData = json.decode(response.body);
            print(jsonData);
            setState(() {
            countData = jsonData;
            });
        } else {
            // Handle error when API request fails
            print('Failed to fetch data: ${response.statusCode}');
        }
        } catch (error) {
        // Handle any exceptions that occur
        print('Error fetching data: $error');
        }
    }

    
  }

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      appBar:  AppBar(
        title: actionBarRow(context),
        centerTitle: false,
        elevation: 0,
        automaticallyImplyLeading: false,
        backgroundColor: Colors.white,
      ),
      body: SafeArea(
        child: Column(
          children: [
            // Divider and space below app bar
            Text(
              'Upcoming Events',
              style: TextStyle(
                fontSize: 18,
                fontWeight: FontWeight.bold,
              ),
            ),
            // Divider(height: 1, thickness: 1, color: Colors.grey),
            SizedBox(height: 10),

            

            // Image Carousel Slider
            CarouselSlider(
              items: carouselImages.map((url) {
                return Image.network(
                  url,
                  fit: BoxFit.cover,
                );
              }).toList(),
              options: CarouselOptions(
                height: 200, // Adjust the height as needed
                viewportFraction: 1.0,
                autoPlay: true, // Enable auto-play
                autoPlayInterval: Duration(seconds: 3), // Set auto-play interval
                onPageChanged: (index, reason) {
                  setState(() {
                    _currentIndex = index;
                  });
                },
              ),
            ),
            // Divider and space below app bar
           
            

            SizedBox(height: 10),
            // Indicator for the current image

            Row(
              mainAxisAlignment: MainAxisAlignment.center,
              children: carouselImages.map((url) {
                int index = carouselImages.indexOf(url);
                return Container(
                  width: 8.0,
                  height: 8.0,
                  margin: EdgeInsets.symmetric(vertical: 10.0, horizontal: 2.0),
                  decoration: BoxDecoration(
                    shape: BoxShape.circle,
                    color: _currentIndex == index ? Colors.blue : Colors.grey,
                  ),
                );
              }).toList(),
            ),
            Text(
              'All Activities',
              style: TextStyle(
                fontSize: 18,
                fontWeight: FontWeight.bold,
              ),
            ),
            // Count Cards
            Row(
              mainAxisAlignment: MainAxisAlignment.spaceEvenly,
              children: [
                CountCard(
                  title: 'Posts',
                  count: countData['_count']['posts'] ?? 0,
                  icon: Icons.description,
                  color: Colors.blue,
                ),
                CountCard(
                  title: 'Comments',
                  count: countData['_count']['comments'] ?? 0,
                  icon: Icons.comment,
                  color: Colors.green,
                ),
                CountCard(
                  title: 'Upvotes',
                  count: countData['_count']['upvotes'] ?? 0,
                  icon: Icons.thumb_up,
                  color: Colors.orange,
                ),
                CountCard(
                  title: 'Feedbacks',
                  count: countData['_count']['feedbacks'] ?? 0,
                  icon: Icons.feedback,
                  color: Colors.red,
                ),
              ],
            ),
          ],
        ),
      ),
    );
  }
}


